import React, { Component } from "react";
import styled from "styled-components";
import johndoe from "./images/john_doe2.jpg";

import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { AddressCard } from "styled-icons/fa-regular/AddressCard";
import { ClipboardCheck } from "styled-icons/fa-solid/ClipboardCheck";
import { Columns } from "styled-icons/fa-solid/Columns";
import { Cubes } from "styled-icons/fa-solid/Cubes";
import { Envelope } from "styled-icons/fa-solid/Envelope";
import { Twitter } from "styled-icons/fa-brands/Twitter";
import { Codepen } from "styled-icons/fa-brands/Codepen";
import { Github } from "styled-icons/fa-brands/Github";
import { Linkedin } from "styled-icons/fa-brands/Linkedin";
import { Medium } from "styled-icons/fa-brands/Medium";
import codesandboxlogo from "./images/codesandboxlogo.png";
const MainDiv = styled.div`
position:relative;

`;

const getIcon = component => {
  return styled(component)`
   margin: -20px 0 0 -8px;
    position: absolute;
    top: 50%;
    left: 50%;
    width: 20px;
    height: 20px;
    font-size: 20px;
    color: #171717;
    line-height: 40px;
    display:inline-block;
    border-radius:1px;
    -webkit-border-radius:1px;
  `;
};

const getSocialIcon = (component, color) => {
  return styled(component)`
  width: 20px;
  height: 20px;
  position:relative;
  font-size:20px;
  display:inline-block;
  color:${color};
  `;
};

const CodeSanBoxImage = () => (
  <img style={{ height: "17px", width: "18px" }} src={codesandboxlogo} />
);
const ProfileCardIcon = getIcon(AddressCard);
const ExperienceIcon = getIcon(ClipboardCheck);
const BlogIcon = getIcon(Columns);
const ProjectIcon = getIcon(Cubes);
const ContactIcon = getIcon(Envelope);
const TwitterIcon = getSocialIcon(Twitter, "#38A1F3");
const CodePenIcon = getSocialIcon(Codepen, "#000");
const GithubIcon = getSocialIcon(Github, "#000");

const LinkedInIcon = getSocialIcon(Linkedin, "#0077B5");
const MediumIcon = getSocialIcon(Medium, "#000");

const CodeSandBoxIcon = getSocialIcon(CodeSanBoxImage, "#000");
const Wrapper = styled.header`
  position: absolute;
  left: 0;
  top: 32rem;
  z-index: 100;

  display:flex;
  width:100%;

  opacity: 1;
  visibility: visible;
  border-radius: 4px;
  -moz-border-radius: 4px;
  -webkit-border-radius: 4px;

  @media (max-width:1024px){

    position:fixed;
    top:0px;
    height:4rem;

    display:flex;
    width:100%;
    align-items:center;
    justify-content:center;
  }
`;

const ProfileCard = styled.div`
  position: relative;
  width: 100%;
  height: 30rem;
  z-index: 11;
  border-radius: 4px;
  -moz-border-radius: 4px;
  -webkit-border-radius: 4px;

  &::before {
    content: "";
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    box-shadow: 10px 10px 15px rgba(0, 0, 0, 0.05);
  }

  &::after {
    content: "";
    position: absolute;
    left: -15px;
    top: -15px;
    width: 100%;
    height: 100%;
    background: linear-gradient(
      135deg,
      rgba(121, 31, 106, 0.4) 0%,
      rgba(46, 202, 127, 0.01) 100%
    );
  }
`;

const Profilediv = styled.div`
  padding: 210px 50px 38px 50px;
  position: center;
  height: 100%;

   @media (max-width:1024px){
      height: 70%;

   }
  background: #ffffff;
  text-align: center;
  z-index: 10;
  border-radius: 4px;
  -moz-border-radius: 4px;
  -webkit-border-radius: 4px;
  font-family: Montserrat;
`;

const ImageCard = styled.div`
  background-image: url(images/bg.jpg);
  position: absolute;
  overflow: hidden;
  left: 0;
  top: 0;
  width: 100%;
  height: 300px;
  background-color: #626262;
  background-repeat: no-repeat;
  background-position: center center;
  background-size: cover;
  border-radius: 4px 4px 0 0;
  -moz-border-radius: 4px 4px 0 0;
  -webkit-border-radius: 4px 4px 0 0;

  &::before {
    content: "";
    position: absolute;
    left: -25%;
    bottom: -198px;
    width: 700px;
    height: 200px;
    background: #ffffff;
    transform: rotate(12deg);
    -webkit-transform: rotate(12deg);
  }

  &::after {
    left: auto;
    right: -25%;
    content: "";
    position: absolute;

    bottom: -198px;
    width: 700px;
    height: 200px;
